function VerReservas(){

    
    const ListaItens = () => {
      // Estado para armazenar a lista de itens
      const [itens, setItens] = useState([]);
    
      // UseEffect para buscar os dados assim que o componente for montado
      useEffect(() => {
        const fetchData = async () => {
          try {
            const response = await axios.get('http://127.0.0.1:8000/cadastropessoa/');
            setItens(response.data); // Atualiza o estado com os dados da API
          } catch (error) {
            console.error('Erro ao buscar dados:', error);
          }
        };
    
        fetchData(); // Chama a função assíncrona
      }, []); // O segundo argumento vazio [] garante que o useEffect será executado apenas uma vez
    
      return (
        <div>
          <h2>Lista de Itens</h2>
          <ul>
            {/* Mapeia os itens e os exibe em uma lista */}
            {itens.map(item => (
              <li key={item.id}>{item.nome}</li>
            ))}
          </ul>
        </div>
      );
    };
    
}
export default VerReservas;
