import React, { useState, useEffect } from 'react';
import axios from 'axios';

function FazerReserva() {
  const [salas, setSalas] = useState([]);
  const [selecionado, setSelecionado] = useState('');
  const [data, setData] = useState('');
  const [locatario, setLocatario] = useState('');

  useEffect(() => {
    const puxar = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/fazer-reserva/');
        setSalas(response.data);
      } catch (error) {
        console.error('Erro ao carregar salas:', error);
      }
    };

    puxar();
  }, []);

  const Cadastrar = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/reservas/', {
        sala: selecionado,
        data: data,
        locatario: locatario,
      });
      console.log('foi', response);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="main">
      <div className="div_cadastro">
        <h3>Tela de Cadastro de Reserva</h3>
        <label>Selecione uma sala:</label>
        <select
          name="sala"
          id="sala"
          value={selecionado}
          onChange={(e) => setSelecionado(e.target.value)}
        >
          <option value="">Selecione...</option>
          {salas.map((sala) => (
            <option key={sala.nome} value={sala.nome}>
              {sala.nome}
            </option>
          ))}
        </select>
        <input
          placeholder="data"
          id="data"
          value={data}
          onChange={(e) => setData(e.target.value)}
        />
        <input
          placeholder="locatario"
          type="number"
          id="locatario"
          value={locatario}
          onChange={(e) => setLocatario(e.target.value)}
        />

        <button onClick={() => Cadastrar()}>Cadastrar</button>
      </div>
    </div>
  );
}

export default FazerReserva;
