import { useState } from "react"
import axios from 'axios'


function Login() {

    const express = require('express');
    const cors = require('cors');
    
    const app = express();
    
    // Configure as opções CORS para permitir o acesso a partir de http://localhost:3000
    const corsOptions = {
      origin: 'http://localhost:3000',
    };
    
    app.use(cors(corsOptions));
    
    // Resto do seu código de servidor aqui
    
    app.listen(3000, () => {
      console.log('Servidor está ouvindo na porta 3000');
    });









    const [state, setState] = useState()
    const [login, setLogin] = useState(true)


    const LoginFunc = async () => {
        let email = document.getElementById("email").value
        let senha = document.getElementById("senha").value

    }
    const CepFunc = async () =>{

        let cep = document.getElementById("cep").value

        if(cep.length==8){
            axios.get(`https://h-apigateway.conectagov.estaleiro.serpro.gov.br/api-cep/v1/consulta/cep/`+cep)
            .then((response) => {
                CepTratarDados(response.data);
              });
        }
    }

    const CepTratarDados = async (dados) =>{
        document.getElementById("estado").innerText = dados.uf
    }

    return (
        <body>
            {login ? (
                <div>
                    <input placeholder="Digite seu email" id="email"></input>
                    <input placeholder="Digite sua senha" id="senha"></input>
                    <button onClick={() =>LoginFunc}>Fazer Login</button><br/>
                    <button onClick={() =>setLogin(false)}>Fazer Cadastro</button>
                </div>
            ) : (
            <div class='div_cadastro'>
                <h3>Tela de Cadastro</h3>
                <input placeholder="CPF"></input>
                <input placeholder="Nome Completo"></input>
                <input placeholder="CEP" id="cep" onKeyUp={CepFunc}></input>
                <input placeholder="Estado"></input>
                <input placeholder="Cidade"></input>
                <input placeholder="Bairro"></input>
                <input placeholder="Rua"></input>
                <input placeholder="Número da Casa"></input>
            </div>
        )}
        </body>

    )


} export default Login
